import React, { Component } from 'react';
import factory from '../ethereum/factory';
import { Card, Button } from "semantic-ui-react";
import Layout from '../components/Layout';
// import Link from '../routes'
import Link from "next/link";

class CampaignIndex extends Component {
    // fetching data on next is different. next render the getInitialProps first and provide to the index
    static async getInitialProps() {
        const campaigns = await factory.methods.getDeployedCampaigns().call();
        return { campaigns };
    }

    //fluid : scratch the box max length of the div
    renderCampaigns() {
        const items = this.props.campaigns.map(campaign => {
            return {
                header: campaign,
                description: (
                    <Link href={'/campaigns/${campaign}'}>
                        <a>View campaign</a>)
                    </Link>
                ),
                fluid: true
            }; 
        })
        //without next line wont display anything
        return <Card.Group items={items} />;
    }

    render(){
        return(
            <Layout>
                <div>
                    <h3>Open Campaigns</h3>
                    <Link href="/campaigns/new">
                        <a>
                            <Button 
                                content="Create Campaign" 
                                icon="add circle" 
                                primary
                                floated="right"
                            />
                        </a>    
                    </Link>
                    {this.renderCampaigns()}
                </div>
            </Layout>
            );
    }
}

export default CampaignIndex;
